
CREATE TABLE ah_seed_table(
    id INT AUTO_INCREMENT PRIMARY KEY,

    -- Core identity
    itemEntry INT UNSIGNED NOT NULL,
    count INT UNSIGNED NOT NULL DEFAULT 1,

    stackable INT UNSIGNED DEFAULT 1,
    maxDurability INT UNSIGNED DEFAULT 0,
    charges TEXT DEFAULT NULL,


    -- Flags
    flags INT UNSIGNED DEFAULT 0,

    -- Auction intent (NOT ownership)
    auctionHouse INT UNSIGNED NOT NULL,
    auctionDuration INT UNSIGNED DEFAULT 43200, -- seconds
    startBid INT UNSIGNED DEFAULT 0,
    buyout INT UNSIGNED DEFAULT 0,

